mapper.select:
SELECT COUNT(USER_ID)
FROM TB_USERMST
WHERE USER_ID = #{user_id}

SELECT * FROM ( SELECT ROW_NUMBER() OVER(ORDER BY USER_REGDATE DESC) AS rn, u.*, tc.COMM_CONTENT FROM TB_USERMST u, TB_COMMON tc WHERE u.USER_COMM_CODE = tc.COMM_CODE AND USER_DELETE_CHECK = 0 AND USER_ID != 'admin'
AND (USER_NM LIKE '%' + #{keyword} + '%' OR USER_ID LIKE '%' + #{keyword} + '%')
AND USER_NM LIKE '%' + #{keyword} + '%'
AND USER_ID LIKE '%' + #{keyword} + '%'
) u2 WHERE u2.rn BETWEEN #{start} AND #{end}

mapper.update:
UPDATE TB_USERMST
SET USER_DELETE_CHECK = 1, USER_UPDATE = CONVERT(CHAR(10), GETDATE(), 23)
WHERE USER_ID = #{user_id}
