mapper.select:
SELECT CONCAT('INI', RIGHT(REPLACE(#{initem_dt},'-',''),6) , FORMAT(ISNULL(COUNT(ti.INITEM_NO),0)+1, '0000')) AS INITEM_NO
FROM TB_INITEM ti
WHERE ti.INITEM_DT = #{initem_dt};

mapper.insert:
INSERT INTO TB_INITEM 
	(INITEM_NO
	,SEQ_NO
	,INITEM_DT
	,PURC_NO
	,CUST_EMP
	,REMARK
	,WH_CD
	,INITEM_EMP_ID
	,INITEM_DELETE_CHK)
VALUES (#{initem_no}
				,(SELECT ISNULL(MAX(ti.SEQ_NO),0)+1 FROM TB_INITEM ti WHERE ti.PURC_NO = #{purc_no})
				,#{initem_dt}
				,#{purc_no}
				,#{cust_emp}
				,#{remark}
				,#{wh_cd}
				,#{initem_emp_id}
				,0
				)

mapper.update:
UPDATE  TB_PURCHASE
SET PURC_STATUS_CHK = #{status}
WHERE PURC_NO = #{purc_no}

MERGE INTO TB_INITEM_ITEM tii USING ( SELECT * FROM ( VALUES
(#{item.initem_no},#{item.purc_no},#{item.item_cd},#{item.qty})
) AS tempTable(INITEM_NO,PURC_NO,ITEM_CD,QTY) ) AS temp ON (tii.INITEM_NO = temp.INITEM_NO AND tii.ITEM_CD = temp.ITEM_CD) WHEN MATCHED THEN UPDATE SET tii.QTY = temp.QTY WHEN NOT MATCHED THEN INSERT (INITEM_NO,PURC_NO,ITEM_CD,QTY) VALUES (temp.INITEM_NO,temp.PURC_NO,temp.ITEM_CD,temp.QTY);
