mapper.select:
select * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY rn ) AS num , sub.rn, sub.outitem_no, sub.seq_no, sub.cust_cd, sub.cust_nm, sub.order_no, sub.order_dt, sub.order_end_dt, sub.order_status_chk, sub.remark, sub.outitem_dt, sub.outitem_user_nm, sub.items FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CAST(SUBSTRING(ou.outitem_no, PATINDEX('%[0-9]%', ou.outitem_no), LEN(ou.outitem_no)) AS BIGINT) DESC) AS rn, ou.outitem_no, ou.cust_cd, ou.seq_no, c.cust_nm, ou.order_no, ord.order_dt, ord.order_end_dt, ord.order_status_chk, u.user_nm as outitem_user_nm, ou.remark, ou.outitem_dt, STUFF(( SELECT ', ' + itemmst.item_nm + ' (' + CAST(oi.qty AS VARCHAR(10)) + '개)' FROM TB_OUTITEM_ITEM oi JOIN TB_ITEMMST itemmst ON oi.item_cd = itemmst.item_cd WHERE ou.outitem_no = oi.outitem_no AND ou.order_no = oi.order_no FOR XML PATH('')), 1, 2, '') AS items FROM TB_OUTITEM ou INNER JOIN TB_CUSTMST c ON ou.cust_cd = c.cust_cd INNER JOIN TB_ORDER ord ON ou.order_no = ord.order_no INNER JOIN TB_USERMST u ON ou.OUTITEM_EMP_ID = u.user_id WHERE ou.outitem_delete_chk = 0 ) AS sub where 1=1
AND sub.items LIKE '%' + #{item_nm} + '%'
AND sub.outitem_dt BETWEEN #{start_day} AND #{end_day}
AND sub.outitem_no LIKE '%' + #{outitem_no} + '%'
AND sub.order_no LIKE '%' + #{order_no} + '%'
AND sub.CUST_NM LIKE '%' + #{cust_nm} + '%'
AND sub.outitem_user_nm LIKE '%' + #{outitem_user_nm} + '%'
) as rt2 WHERE 1=1
and rt2.num>=#{start}
and rt2.num<=#{end}

select count(*) FROM ( SELECT ROW_NUMBER() OVER (ORDER BY rn ) AS num , sub.rn, sub.outitem_no, sub.seq_no, sub.cust_cd, sub.cust_nm, sub.order_no, sub.order_dt, sub.order_end_dt, sub.order_status_chk, sub.remark, sub.outitem_dt, sub.outitem_user_nm, sub.items FROM ( SELECT ROW_NUMBER() OVER (ORDER BY CAST(SUBSTRING(ou.outitem_no, PATINDEX('%[0-9]%', ou.outitem_no), LEN(ou.outitem_no)) AS BIGINT) DESC) AS rn, ou.outitem_no, ou.cust_cd, ou.seq_no, c.cust_nm, ou.order_no, ord.order_dt, ord.order_end_dt, ord.order_status_chk, u.user_nm as outitem_user_nm, ou.remark, ou.outitem_dt, STUFF(( SELECT ', ' + itemmst.item_nm + ' (' + CAST(oi.qty AS VARCHAR(10)) + '개)' FROM TB_OUTITEM_ITEM oi JOIN TB_ITEMMST itemmst ON oi.item_cd = itemmst.item_cd WHERE ou.outitem_no = oi.outitem_no AND ou.order_no = oi.order_no FOR XML PATH('')), 1, 2, '') AS items FROM TB_OUTITEM ou INNER JOIN TB_CUSTMST c ON ou.cust_cd = c.cust_cd INNER JOIN TB_ORDER ord ON ou.order_no = ord.order_no INNER JOIN TB_USERMST u ON ou.OUTITEM_EMP_ID = u.user_id WHERE ou.outitem_delete_chk = 0 ) AS sub where 1=1
AND sub.items LIKE '%' + #{item_nm} + '%'
AND sub.outitem_dt BETWEEN #{start_day} AND #{end_day}
AND sub.outitem_no LIKE '%' + #{outitem_no} + '%'
AND sub.order_no LIKE '%' + #{order_no} + '%'
AND sub.CUST_NM LIKE '%' + #{cust_nm} + '%'
AND sub.outitem_user_nm LIKE '%' + #{outitem_user_nm} + '%'
) as rt2 WHERE 1=1
and rt2.num>=#{start}
and rt2.num<=#{end}


SELECT COUNT(outitem_no) 
FROM tb_outitem
WHERE order_no = #{order_no}


SELECT COUNT(*) 
FROM tb_outitem_item
WHERE outitem_no = #{outitem_no}
AND order_no = #{order_no}


select count(*)
from TB_OUTITEM_ITEM as oui
join TB_OUTITEM as ou on oui.OUTITEM_NO = ou.OUTITEM_NO 
where oui.outitem_no=#{outitem_no}
and oui.order_no=#{order_no}
and oui.ITEM_CD =#{item_cd}
and ou.OUTITEM_DELETE_CHK =0

mapper.update:
UPDATE TB_outitem_item
SET 
qty = ISNULL(#{qty}, qty)
WHERE outitem_no = #{outitem_no}	
and order_no=#{order_no}
and item_cd=#{item_cd}

mapper.delete:
DELETE FROM tb_outitem_item 
WHERE outitem_no = #{outitem_no} 
  AND order_no = #{order_no} 
  AND item_cd = #{item_cd}
